package s0413;

public class Example06 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num1 = 3;
		int num2 = 7;
		
		if(++num1<5 | ++num2 > 8)
			System.out.println(num1);
		System.out.println(num2);
		
		System.out.println();
		num1 = 3;
		num2 = 7;
		
		if(++num1<5 | ++num2 > 8)
			System.out.println(num1);
		System.out.println(num2);
	}

}
